(function() {
  var api, config;

  config = require('./config');

  api = require('./api');

  this.getToken = function(username, password, cb) {
    return api.post('auth-token', {
      username: username,
      password: password
    }, function(err, res) {
      var token;
      if (err) {
        throw err;
      }
      token = JSON.parse(res).token;
      return cb(token);
    });
  };

  module.exports = this;

}).call(this);
