(function() {
  var api;

  api = require('./api');

  this.list = function(token, cb) {
    return api.get('repos', null, token, function(err, res) {
      if (cb) {
        return cb(err, res);
      }
    });
  };

  this.create = function(token, params, cb) {
    params.name = params.name || 'New Library';
    params.desc = params.desc || 'New Library';
    return api.post('repos', params, token, function(err, res) {
      if (cb) {
        return cb(err, res);
      }
    });
  };

  module.exports = this;

}).call(this);
